status => {
  if (status === 'CANCELLED') {
    return 'RGB(255, 0, 0)';
  }
  ;
  if (status === 'UNFULFILLED' || status === 'PACKED') {
    return 'RGB(255, 165, 0)';
  }
  ;
  if (status === 'SHIPPED' || status === 'DELIVERED') {
    return 'RGB(0, 128, 0)';
  }
  return 'RGB(255, 165, 0)';
}

