			fieldValue = typeof fieldValue == 'number' ? JSON.stringify(fieldValue) : fieldValue;
			doc.setFont(field.font, field.fontStyle);
			doc.setFontSize(field.fontSize);
			doc.text(fieldValue, field.x, field.y);
