		const newImgSize = {w:parseInt(imageSize.width*wRatio),h:parseInt(imageSize.height*wRatio)};
		await doc.addImage(pdfData, 'JPEG', 0, 0, newImgSize.w, newImgSize.h);

		//Set fonts and style
