			return [{
				id: 1,
				name: 'Jamison Yard',
			},
							{
								id: 2,
								name: 'Brit Avenue',
							}
						 ]
		}

		const uniqueWarehousesRaw = {}

		for (let i = 0; i < sanitisedWarehouses.length; i++) {
